import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { Plus, Trash2, Edit, Save, X, HelpCircle, Image, Type, ArrowUp, ArrowDown } from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

const QuestionManagement = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [languages, setLanguages] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [deletingId, setDeletingId] = useState(null);
  const [questionContent, setQuestionContent] = useState({});
  const [optionsContent, setOptionsContent] = useState([{ content: {} }, { content: {} }, { content: {} }, { content: {} }]);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    setValue,
    watch
  } = useForm();

  const watchedQuestionType = watch('question_type');

  useEffect(() => {
    fetchInitialData();
  }, []);

  useEffect(() => {
    if (selectedQuiz) {
      fetchQuestions();
    }
  }, [selectedQuiz]);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      const [quizzesResponse, languagesResponse] = await Promise.all([
        axios.get('/api/quizzes'),
        axios.get('/api/languages/active')
      ]);
      setQuizzes(quizzesResponse.data);
      setLanguages(languagesResponse.data);
      
      if (quizzesResponse.data.length > 0) {
        setSelectedQuiz(quizzesResponse.data[0].id.toString());
      }
      if (languagesResponse.data.length > 0) {
        setSelectedLanguage(languagesResponse.data[0].code);
      }
    } catch (error) {
      console.error('Failed to fetch initial data:', error);
      toast.error('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const fetchQuestions = async () => {
    try {
      const response = await axios.get(`/api/quizzes/${selectedQuiz}`);
      setQuestions(response.data.questions || []);
    } catch (error) {
      console.error('Failed to fetch questions:', error);
      toast.error('Failed to load questions');
    }
  };

  const onSubmit = async (data) => {
    try {
      // Prepare content array from current state
      const content = Object.entries(questionContent).map(([langCode, content]) => ({
        language_code: langCode,
        question_text: content.question_text || '',
        media_url: content.media_url || null,
        explanation: content.explanation || null
      }));

      // Prepare options array from current state
      const options = optionsContent.map((option, index) => ({
        order_index: index + 1,
        is_correct: option.is_correct || false,
        content: Object.entries(option.content).map(([langCode, optContent]) => ({
          language_code: langCode,
          option_text: optContent.option_text || '',
          media_url: optContent.media_url || null
        }))
      }));

      const questionData = {
        quiz_id: parseInt(selectedQuiz),
        question_type: data.question_type,
        order_index: questions.length + 1,
        content,
        options
      };

      if (editingId) {
        await axios.put(`/api/questions/${editingId}`, questionData);
        toast.success('Question updated successfully');
        setEditingId(null);
      } else {
        await axios.post('/api/questions', questionData);
        toast.success('Question created successfully');
      }
      
      setShowAddForm(false);
      reset();
      setQuestionContent({});
      setOptionsContent([{ content: {} }, { content: {} }, { content: {} }, { content: {} }]);
      fetchQuestions();
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to save question';
      toast.error(message);
    }
  };

  const handleEdit = (question) => {
    setEditingId(question.id);
    setValue('question_type', question.question_type);
    
    // Set question content
    languages.forEach(lang => {
      const content = question.content[lang.code];
      if (content) {
        setValue(`question_text_${lang.code}`, content.question_text);
        setValue(`media_url_${lang.code}`, content.media_url);
        setValue(`explanation_${lang.code}`, content.explanation);
      }
    });

    // Set options
    question.options.forEach((option, index) => {
      setValue(`is_correct_${index}`, option.is_correct);
      languages.forEach(lang => {
        const optionContent = option.content[lang.code];
        if (optionContent) {
          setValue(`option_text_${index}_${lang.code}`, optionContent.option_text);
          setValue(`option_media_url_${index}_${lang.code}`, optionContent.media_url);
        }
      });
    });

    setShowAddForm(true);
  };

  const handleDelete = async (questionId) => {
    if (!window.confirm('Are you sure you want to delete this question?')) {
      return;
    }

    try {
      setDeletingId(questionId);
      await axios.delete(`/api/questions/${questionId}`);
      toast.success('Question deleted successfully');
      fetchQuestions();
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to delete question';
      toast.error(message);
    } finally {
      setDeletingId(null);
    }
  };

  const handleCancel = () => {
    setShowAddForm(false);
    setEditingId(null);
    reset();
    setQuestionContent({});
    setOptionsContent([{ content: {} }, { content: {} }, { content: {} }, { content: {} }]);
  };

  // Helper functions for managing content
  const updateQuestionContent = (field, value) => {
    setQuestionContent(prev => ({
      ...prev,
      [selectedLanguage]: {
        ...prev[selectedLanguage],
        [field]: value
      }
    }));
  };

  const updateOptionContent = (optionIndex, field, value) => {
    setOptionsContent(prev => {
      const newOptions = [...prev];
      newOptions[optionIndex] = {
        ...newOptions[optionIndex],
        content: {
          ...newOptions[optionIndex].content,
          [selectedLanguage]: {
            ...newOptions[optionIndex].content[selectedLanguage],
            [field]: value
          }
        }
      };
      return newOptions;
    });
  };

  const updateOptionCorrect = (optionIndex, isCorrect) => {
    setOptionsContent(prev => {
      const newOptions = [...prev];
      newOptions[optionIndex] = {
        ...newOptions[optionIndex],
        is_correct: isCorrect
      };
      return newOptions;
    });
  };

  const handleImageUpload = (type, optionIndex = null) => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'image/*';
    input.onchange = (e) => {
      const file = e.target.files[0];
      if (file) {
        // For now, we'll just show the file name
        // In a real app, you'd upload to a server and get a URL
        const fileName = file.name;
        if (type === 'question') {
          updateQuestionContent('media_url', fileName);
        } else if (type === 'option' && optionIndex !== null) {
          updateOptionContent(optionIndex, 'media_url', fileName);
        }
        toast.success('Image selected: ' + fileName);
      }
    };
    input.click();
  };

  const getLanguageName = (code) => {
    const language = languages.find(lang => lang.code === code);
    return language ? language.native_name : code;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div>
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <HelpCircle className="w-8 h-8 text-indigo-600" />
          <h2 className="text-2xl font-bold text-gray-900">Question Management</h2>
        </div>
        <button
          onClick={() => setShowAddForm(!showAddForm)}
          className="btn btn-primary"
          disabled={!selectedQuiz}
        >
          <Plus className="w-4 h-4" />
          Add Question
        </button>
      </div>

      {/* Quiz Selection */}
      <div className="card mb-6">
        <div className="card-header">
          <h3 className="card-title">Select Quiz</h3>
        </div>
        <div className="form-group">
          <label className="form-label">Quiz</label>
          <select
            value={selectedQuiz}
            onChange={(e) => setSelectedQuiz(e.target.value)}
            className="form-input"
          >
            <option value="">Select a quiz</option>
            {quizzes.map((quiz) => (
              <option key={quiz.id} value={quiz.id}>
                {quiz.title}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Add/Edit Question Form */}
      {showAddForm && selectedQuiz && (
        <div className="card mb-6">
          <div className="card-header">
            <h3 className="card-title">
              {editingId ? 'Edit Question' : 'Add New Question'}
            </h3>
          </div>
          <form onSubmit={handleSubmit(onSubmit)}>
            {/* Question Type */}
            <div className="form-group">
              <label className="form-label">Question Type</label>
              <div className="flex gap-4">
                <label className="flex items-center">
                  <input
                    type="radio"
                    value="text"
                    className="form-radio"
                    {...register('question_type', { required: 'Question type is required' })}
                  />
                  <span className="ml-2 flex items-center">
                    <Type className="w-4 h-4 mr-1" />
                    Text Only
                  </span>
                </label>
                <label className="flex items-center">
                  <input
                    type="radio"
                    value="media"
                    className="form-radio"
                    {...register('question_type', { required: 'Question type is required' })}
                  />
                  <span className="ml-2 flex items-center">
                    <Image className="w-4 h-4 mr-1" />
                    Text + Media
                  </span>
                </label>
              </div>
              {errors.question_type && (
                <p className="error-message">{errors.question_type.message}</p>
              )}
            </div>

            {/* Language Selection */}
            <div className="form-group">
              <label className="form-label">Select Language to Add Content</label>
              <select
                value={selectedLanguage}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                className="form-input"
              >
                {languages.map((language) => (
                  <option key={language.code} value={language.code}>
                    {language.native_name} ({language.name})
                  </option>
                ))}
              </select>
            </div>

            {/* Current Language Content */}
            <div className="border rounded-lg p-4 mb-4 bg-gray-50">
              <h4 className="font-medium text-lg mb-3 flex items-center gap-2">
                <span className="w-3 h-3 bg-indigo-500 rounded-full"></span>
                {getLanguageName(selectedLanguage)} Content
              </h4>
              
              {/* Question Content */}
              <div className="form-group">
                <label className="form-label">Question Text</label>
                <textarea
                  rows={3}
                  className="form-input"
                  placeholder={`Enter question in ${getLanguageName(selectedLanguage)}`}
                  value={questionContent[selectedLanguage]?.question_text || ''}
                  onChange={(e) => updateQuestionContent('question_text', e.target.value)}
                />
              </div>

              {watchedQuestionType === 'media' && (
                <div className="form-group">
                  <label className="form-label">Question Media</label>
                  <div className="flex gap-2">
                    <input
                      type="url"
                      className="form-input flex-1"
                      placeholder="Enter image or video URL"
                      value={questionContent[selectedLanguage]?.media_url || ''}
                      onChange={(e) => updateQuestionContent('media_url', e.target.value)}
                    />
                    <button
                      type="button"
                      onClick={() => handleImageUpload('question')}
                      className="btn btn-outline"
                    >
                      <Image className="w-4 h-4" />
                      Upload Image
                    </button>
                  </div>
                </div>
              )}

              <div className="form-group">
                <label className="form-label">Explanation (Optional)</label>
                <textarea
                  rows={2}
                  className="form-input"
                  placeholder={`Enter explanation in ${getLanguageName(selectedLanguage)}`}
                  value={questionContent[selectedLanguage]?.explanation || ''}
                  onChange={(e) => updateQuestionContent('explanation', e.target.value)}
                />
              </div>

              {/* Options */}
              <div className="mt-4">
                <h5 className="font-medium mb-3">Options</h5>
                {[0, 1, 2, 3].map((optionIndex) => (
                  <div key={optionIndex} className="border rounded-lg p-3 mb-3 bg-white">
                    <div className="flex items-center gap-3 mb-2">
                      <input
                        type="radio"
                        name="correct_option"
                        className="form-radio"
                        checked={optionsContent[optionIndex]?.is_correct || false}
                        onChange={() => {
                          // Uncheck all other options
                          setOptionsContent(prev => prev.map((opt, idx) => ({
                            ...opt,
                            is_correct: idx === optionIndex
                          })));
                        }}
                      />
                      <span className="font-medium">Option {optionIndex + 1}</span>
                      {optionsContent[optionIndex]?.is_correct && (
                        <span className="text-green-600 text-sm font-medium">(Correct Answer)</span>
                      )}
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Option Text</label>
                      <input
                        type="text"
                        className="form-input"
                        placeholder={`Option ${optionIndex + 1} in ${getLanguageName(selectedLanguage)}`}
                        value={optionsContent[optionIndex]?.content[selectedLanguage]?.option_text || ''}
                        onChange={(e) => updateOptionContent(optionIndex, 'option_text', e.target.value)}
                      />
                    </div>

                    {watchedQuestionType === 'media' && (
                      <div className="form-group">
                        <label className="form-label">Option Media</label>
                        <div className="flex gap-2">
                          <input
                            type="url"
                            className="form-input flex-1"
                            placeholder="Enter image URL"
                            value={optionsContent[optionIndex]?.content[selectedLanguage]?.media_url || ''}
                            onChange={(e) => updateOptionContent(optionIndex, 'media_url', e.target.value)}
                          />
                          <button
                            type="button"
                            onClick={() => handleImageUpload('option', optionIndex)}
                            className="btn btn-outline"
                          >
                            <Image className="w-4 h-4" />
                            Upload
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>

            {/* Language Progress */}
            <div className="mb-4">
              <h5 className="font-medium mb-2">Content Progress</h5>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
                {languages.map((language) => {
                  const hasContent = questionContent[language.code]?.question_text;
                  const hasOptions = optionsContent.some(opt => opt.content[language.code]?.option_text);
                  return (
                    <div key={language.code} className={`p-2 rounded text-sm ${hasContent && hasOptions ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-600'}`}>
                      {language.native_name}
                      {hasContent && hasOptions && <span className="ml-1"></span>}
                    </div>
                  );
                })}
              </div>
            </div>

            <div className="flex gap-2 mt-6">
              <button type="submit" className="btn btn-primary">
                <Save className="w-4 h-4" />
                {editingId ? 'Update Question' : 'Create Question'}
              </button>
              <button
                type="button"
                onClick={handleCancel}
                className="btn btn-outline"
              >
                <X className="w-4 h-4" />
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Questions List */}
      {selectedQuiz && (
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">
              Questions ({questions.length})
            </h3>
          </div>
          <div className="space-y-4 p-4">
            {questions.map((question, index) => (
              <div key={question.id} className="border rounded-lg p-4">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center gap-3">
                    <span className="bg-indigo-100 text-indigo-800 px-2 py-1 rounded text-sm font-medium">
                      Q{index + 1}
                    </span>
                    <span className={`badge ${question.question_type === 'text' ? 'user' : 'admin'}`}>
                      {question.question_type === 'text' ? (
                        <>
                          <Type className="w-3 h-3 mr-1" />
                          Text
                        </>
                      ) : (
                        <>
                          <Image className="w-3 h-3 mr-1" />
                          Media
                        </>
                      )}
                    </span>
                  </div>
                  <div className="flex gap-2">
                    <button
                      onClick={() => handleEdit(question)}
                      className="btn btn-outline btn-sm"
                      title="Edit question"
                    >
                      <Edit className="w-4 h-4" />
                    </button>
                    <button
                      onClick={() => handleDelete(question.id)}
                      disabled={deletingId === question.id}
                      className="btn btn-danger btn-sm"
                      title="Delete question"
                    >
                      {deletingId === question.id ? (
                        <div className="spinner"></div>
                      ) : (
                        <Trash2 className="w-4 h-4" />
                      )}
                    </button>
                  </div>
                </div>

                {/* Question Content Preview */}
                <div className="space-y-2">
                  {Object.entries(question.content).map(([langCode, content]) => (
                    <div key={langCode} className="text-sm">
                      <span className="font-medium text-gray-600">
                        {getLanguageName(langCode)}:
                      </span>
                      <span className="ml-2">{content.question_text}</span>
                    </div>
                  ))}
                </div>

                {/* Options Preview */}
                <div className="mt-3">
                  <div className="text-sm font-medium text-gray-600 mb-2">Options:</div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                    {question.options.map((option, optIndex) => (
                      <div key={option.id} className={`p-2 rounded text-sm ${option.is_correct ? 'bg-green-50 border border-green-200' : 'bg-gray-50'}`}>
                        <div className="flex items-center gap-2">
                          <span className={`w-2 h-2 rounded-full ${option.is_correct ? 'bg-green-500' : 'bg-gray-300'}`}></span>
                          <span className="font-medium">Option {optIndex + 1}</span>
                          {option.is_correct && <span className="text-green-600 text-xs">(Correct)</span>}
                        </div>
                        {Object.entries(option.content).map(([langCode, optContent]) => (
                          <div key={langCode} className="text-xs text-gray-600 ml-4">
                            {getLanguageName(langCode)}: {optContent.option_text}
                          </div>
                        ))}
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            ))}
            {questions.length === 0 && (
              <div className="text-center py-8 text-gray-500">
                <HelpCircle className="w-12 h-12 mx-auto mb-4 opacity-50" />
                <p>No questions found. Add your first question to get started.</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default QuestionManagement;
